// import styled, {css} from 'styled-components';
import styled from 'styled-components';

import { Link } from 'react-router-dom';

// to reduce more amount of code, we can use "as" property in page where these css applies
// to do so, remove {css} from import and comment 'OptionContainerStyles'

// const OptionContainerStyles = css`
//     padding: 10px 15px;
//     cursor: pointer;
// `;

export const HeaderContainer = styled.div`
    height: 70px;
    width: 100%;
    display: flex;
    justify-content: space-between;
    margin-bottom: 25px;
`;

export const LogoContainer = styled(Link)`
    height: 100%;
    width: 70px;
    padding: 25px;
`;

export const OptionContainer = styled.div`
    width: 50%;
    height: 100%;
    display: flex;
    align-items: center;
    justify-content: flex-end;
`;

// code that we comment above will use in here while creating a single call of either link or Div
// changes we can utitlies in calling place
export const OptionLink = styled(Link)`
    padding: 10px 15px;
    cursor: pointer;
`;

